/**Types of blocks:
 * 0 Air
 * 1 Stone
 * 2 Water
 * 3 Fixed water
 */

/** Function takes in grid, coordinates and type of block to be placed
 * returns boolean representing success / failure
 */
class GameGrid {

    constructor(props) {
        this.grid = intializeGrid(props.size);
    }

    placeBlock(grid, x, y, type) {
        if (grid[x][y] == 0) {
            grid[x][y] = type;
            return true;
        } else {
            return false;
        }
    }

    /** Function takes in grid and replaces water blocks with air blocks
     */
    resetGrid(grid) {
        for (let x = 0; x < grid.length; x++) {
            for (let y = 0; y < grid.length; y++) {
                if (grid[x][y] == 2) {
                    grid[x][y] = 0;
                }
            }
        }
    }

    intializeGrid(size) {
        var grid = new Array(size);
        for (var i = 0; i < size; i++) {
            grid[i] = new Array(size);
            for(var j=0; j<size; j++) {
                grid[i][j] = 0;
            }
        }
        return grid;
    }

    numToIcon = {0:" ", 1:"#", 2:"o", 3:"o"};

    printGrid(grid) {
        process.stdout.write("----------------\n");
        for(var i=0; i<grid.length; i++) {
            for(var j=0; j<grid[i].length; j++) {
                process.stdout.write(numToIcon[grid[i][j]] + " ");
            }
            process.stdout.write("|\n");
        }
        process.stdout.write("----------------\n");
    }

    updateGrid(grid) {
        for(var i=0; i<grid.length-1; i++) {
            for(var j=0; j<grid[i].length; j++) {
                if (grid[i][j] == 2) {
                    if (grid[i+1][j] == 0) {
                        grid[i+1][j] = 4;
                    } else if (grid[i+1][j] == 1) {
                        if (grid[i][j-1] == 0) grid[i][j-1] = 4;
                        if (grid[i][j+1] == 0) grid[i][j+1] = 4;
                    }
                    grid[i][j] = 3;
                }
            }
        }
        for(var i=0; i<grid.length; i++) {
            for(var j=0; j<grid[i].length; j++) {
                if (grid[i][j] == 4) grid[i][j] = 2;
            }
        }

    }

    placeWater(grid, x, y) {
        grid[x][y] = 2;
    }
    // test code end

    done(grid) {
        for (let i = 0; i < grid[0].length; i++) {
            if (grid[grid.length][i] === 2 || grid[grid.length][i] === 3) {
                return true
            }
        }
        return false
    }

    placeStone(grid, x, y) {
        grid[x][y] = 1;
    }
}

export default GameGrid
